// Extract and validate character generation parameters
const inputData = $input.all()[0].json.body;

// Required fields from YAML template
const genre = inputData.genre;
const storyContext = inputData.story_context;
const numCharacters = inputData.num_characters;
const characterRequirements = inputData.character_requirements;
const language = inputData.language || 'English';

if (!genre || !storyContext || !numCharacters || !characterRequirements) {
  throw new Error('Genre, story_context, num_characters, and character_requirements are required');
}

// Build character generation prompt based on YAML template
let prompt = `Generate detailed character profiles for this ${genre} story.\n\n`;
prompt += `Story context: ${storyContext}\n`;
prompt += `Number of characters: ${numCharacters}\n`;
prompt += `Character requirements: ${characterRequirements}\n\n`;

prompt += `For each character, provide:\n`;
prompt += `- Name and basic demographics\n`;
prompt += `- Personality traits and quirks\n`;
prompt += `- Background and motivation\n`;
prompt += `- Character arc and development\n`;
prompt += `- Relationships with other characters\n`;
prompt += `- Dialogue style and voice\n\n`;

prompt += `Output in JSON format with character array.\n`;
prompt += `Language: ${language}`;

const promptData = {
  genre: genre,
  story_context: storyContext,
  num_characters: numCharacters,
  character_requirements: characterRequirements,
  language: language,
  prompt: prompt
};

return [{ json: promptData }];

// Process and format the character generation response
const promptData = $('Prepare Character Prompt').all()[0].json;
const aiResponse = $input.all()[0].json.response;

// Clean up the response and parse JSON if possible
let charactersData = aiResponse || '';
let parsedCharacters = null;

try {
  // Try to extract JSON from the response
  const jsonMatch = charactersData.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    parsedCharacters = JSON.parse(jsonMatch[0]);
  }
} catch (error) {
  // If JSON parsing fails, keep raw response
  console.log('Could not parse JSON, keeping raw response');
}

const finalResult = {
  genre: promptData.genre,
  story_context: promptData.story_context,
  num_characters: promptData.num_characters,
  character_requirements: promptData.character_requirements,
  language: promptData.language,
  characters: parsedCharacters || charactersData,
  generated_at: new Date().toISOString()
};

return [{ json: finalResult }];
