parameters.jsCode:
// Process and format the scene generation response
const inputData = $('Webhook').all()[0].json.body;
const aiResponse = $input.all()[0].json.response;

// Clean up the response
let sceneContent = aiResponse || '';

// Count words in generated scene
const wordCount = sceneContent.split(/\s+/).filter(word => word.length > 0).length;

const finalResult = {
  scene_number: inputData.scene_number,
  chapter_number: inputData.chapter_number,
  book_title: inputData.book_title,
  scene_outline: inputData.scene_outline,
  scene_purpose: inputData.scene_purpose,
  scene_characters: inputData.scene_characters,
  scene_setting: inputData.scene_setting,
  language: inputData.language || 'English',
  scene_content: sceneContent,
  word_count: wordCount,
  generated_at: new Date().toISOString()
};

return [{ json: finalResult }];
