parameters.jsCode:
// Process and format the worldbuilding response
const inputData = $('Webhook').all()[0].json.body;
const aiResponse = $input.all()[0].json.response;

// Try to parse JSON from the AI response
let worldbuildingData = aiResponse || '';
let parsedWorldbuilding = null;

try {
  // Look for JSON in the response
  const jsonMatch = worldbuildingData.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    parsedWorldbuilding = JSON.parse(jsonMatch[0]);
  }
} catch (error) {
  console.log('Could not parse JSON, keeping raw response');
}

const finalResult = {
  category: inputData.category,
  genre: inputData.genre,
  project_context: inputData.project_context,
  worldbuilding_aspects: inputData.worldbuilding_aspects,
  language: inputData.language || 'English',
  worldbuilding_data: parsedWorldbuilding || worldbuildingData,
  created_at: new Date().toISOString()
};

return [{ json: finalResult }];
